import "./Tarjeta.css";
import data from "../../data/data.json"
import { useEffect, useState } from "react";

export const Tarjeta = (props) =>{

    const [productos,setProductos] = useState([]);

    const pedirProducto = () =>{
        return new Promise ((resolve, reject) => {
            resolve(data)      
        })
    }

    useEffect(()=> {
        pedirProducto()
        .then ((res) =>{
            setProductos(res);
        })
    },[]) 

    return (
        <>
        {
            productos.length > 0 &&

            productos.map((producto) => {
                return (
                    <div className="tarjeta">
                    <img className="tarjeta-img" src={producto.imagen} alt={producto[1].nombre}/>
        
                    <div className="tarjeta_contenido">
                        <h3>{producto.nombre}</h3>
                        <p>Precio: {producto.precio}</p>
                        <p>{producto.info}</p>
                    </div>
        
                    <div className="tarjeta-btn">
                    <button className="btn" onClick={props.funcion}>AÃ±adir a favoritos <i class="fa-solid fa-star"></i></button>
                    </div>
         
                </div>
                )
            })

        }
        </>
    )
}
