import "./Tarjeta.css";
import data from "../../data/data.json"
import { useEffect, useState } from "react";

export const Tarjeta = (props) =>{

    const [productos,setProductos] = useState([]);

    const pedirProducto = () =>{
        return new Promise ((resolve, reject) => {
            resolve(data)      
        })
    }

    useEffect(()=> {
        pedirProducto()
        .then ((res) =>{
            setProductos(res);
        })
    },[]) 

    return (
        <>
        {
            productos.length > 0 &&
        
        <div className="tarjeta">
            <img className="tarjeta-img" src={productos[1].imagen} alt={productos[1].nombre}/>

            <div className="tarjeta_contenido">
                <h3>{productos[1].nombre}</h3>
                <p>Precio: {productos[1].precio}</p>
                <p>{productos[1].info}</p>
            </div>

            <div className="tarjeta-btn">
            <button className="btn" onClick={props.funcion}>AÃ±adir a favoritos <i class="fa-solid fa-star"></i></button>
            </div>
 
        </div>
        }

        </>
    )
}
