import { useEffect, useState } from "react"
import { pedirProducto } from "../../helper/pedirdatos";
import { ProductoDetalle } from "../ProductoDetalle/ProductoDetalle";

export  const Producto = (productoId) =>{
    const [producto, setProductos] = useState(null);

    useEffect(()=>{
        pedirProducto(productoId)
        .then((res) => {
            setProductos(res);
        })
    },[productoId])
    return (
        <div>
            {producto && <ProductoDetalle producto={producto}/>}
        </div>
    )
}
